<script setup>
import { login as accountLogin} from '@/services/accountsService';
import { onMounted, ref, watch } from 'vue';
import * as app from '@/stores/app-store';
import router from '@/router';

let form = ref({});
let hasLogged = ref(null);

onMounted(() => {
      let data = app.app().get();
      hasLogged.value = data.token;
})

const login = async () => {
      let data = await accountLogin(form.value);
      
      app.app().set({
            'token' : data.token,
            'user' : data.user
      });
}

watch(hasLogged, (v) => {
      if (v) {
            router.push('/events');
      }
});

</script>

